import { useRef } from "react";

interface Coordinate {
  xmax: {
    [key: number]: number;
  };
  xmin: {
    [key: number]: number;
  };
  ymax: {
    [key: number]: number;
  };
  ymin: {
    [key: number]: number;
  };
  length: number;
}

type Canvas = {
  camRef: any;
  zindex: number;
  loc: Coordinate | undefined;
};

const CanvasComponent: React.FC<Canvas> = ({ camRef, zindex, loc }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  if (camRef.current) {
    console.log(camRef.current["width"]);
  }
  if (loc) {
    console.log(loc.xmax[0]);
  }
  return (
    <canvas
      ref={canvasRef}
      style={{
        position: "absolute",
        marginLeft: "auto",
        marginRight: "auto",
        left: 0,
        right: 0,
        width: 640,
        height: 480,
        zIndex: zindex,
      }}
    />
  );
};

export default CanvasComponent;
