import React, { useEffect, useRef, useState } from "react";
import "./App.css";
import WebcamComponent from "./components/WebcamComponent";
import CanvasComponent from "./components/CanvasComponent";
import Webcam from "react-webcam";
import axios from "axios";

interface Coordinate {
  xmax: object;
  xmin: object;
  ymax: object;
  ymin: object;
}

function App() {
  const webcamRef = useRef<Webcam>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [countedPeople, setCountedPeople] = useState<number>(0);
  const [coordinate, setCoordinate] = useState<object>();

  const callAPI = () => {
    if (webcamRef.current && webcamRef.current.getScreenshot()) {
      // var image = new Image();
      // image.src = webcamRef.current.getScreenshot()!;
      // document.body.appendChild(image);
      var formData = new FormData();
      formData.append("img", webcamRef.current.getScreenshot()!);

      axios({
        method: "post",
        url: "http://localhost:8000/",
        data: formData,
        headers: { "Content-Type": "multipart/form-data" },
      })
        .then((response) => {
          setCountedPeople(Object.keys(response.data.class).length);
          const obj: { xmax: object; xmin: object } = { xmax: {}, xmin: {} };
          obj.xmax = response.data.xmax;
          obj.xmin = response.data.xmin;
          console.log(obj);
        })
        .catch((error) => {
          console.log(error.response);
        });
    }
  };

  useEffect(() => {
    const interval = setInterval(() => {
      callAPI();
    }, 5000);
    return () => clearInterval(interval);
  });

  return (
    <div className="App">
      <header className="App-header">
        <WebcamComponent camRef={webcamRef} />
        <CanvasComponent canvRef={canvasRef} zindex={8} />
      </header>
      <h3 style={{ position: "relative", color: "black" }}>{countedPeople}</h3>
    </div>
  );
}

export default App;
